import React from "react";
import { Routes, Route } from "react-router-dom";

import Wrapper from "./wrapper";
import AuthorizationContainer from "./components/authorization/authorization-container";
import RegistrationContainer from "./components/authorization/registration-container";
import PrivacyInfo from "./components/personal/privacy-info";
import Orders from "./components/personal/orders";
import TermsService from "./components/personal/terms-service";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogged: false,
      authUser: null
    }
  }

  handleLogIn = (data) => {
    this.setState({ isLogged: true, authUser: data });
  }

  handleLogOut = () => {
    this.setState({ isLogged: false });
  }

  render() {
    const { isLogged, authUser } = this.state;

    return (
      <div className='wrapper'>
        <Routes>
          <Route path='*' element={<Wrapper {...this.props} isLogged={isLogged} authUser={authUser} />} />
          <Route path='/login/*' element={<AuthorizationContainer handleLogIn={this.handleLogIn} isLogged={isLogged} authUser={authUser} />} />
          <Route path='/registration/*' element={<RegistrationContainer handleLogIn={this.handleLogIn} />} />
          {isLogged ?
            <>
              <Route path='/personal-area/:user/info/*' element={<PrivacyInfo {...this.props} isLogged={isLogged} authUser={authUser} />} />
              <Route path='/personal-area/:user/orders/*' element={<Orders {...this.props} isLogged={isLogged} authUser={authUser} />} />
              <Route path='/personal-area/:user/terms/*' element={<TermsService {...this.props} isLogged={isLogged} authUser={authUser} />} />
            </>
            :
            <Route path='/login/*' element={<AuthorizationContainer handleLogIn={this.handleLogIn} isLogged={isLogged} authUser={authUser} />} />
          }
        </Routes>
      </div>
    )
  }
}

export default App;
