import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";

import AuthorizationForm from "./authorization-form";
import RegistrationForm from "./registration-form";

const Authorization = () => {

    const [authToggle, changeStateToogle] = useState(true);
    const hist = useLocation();

    return (
        <section className='authorization__wrapper'>
            <div className='authorization__img'>
                <Link to='/'><img src='../images/logo.svg' alt='logo' /></Link>
            </div>
            <div className='authorization-form__wrapper'>
                <div className='authorization__toggle'>
                    <button
                        className={authToggle ? 'active' : ''}
                        onClick={() => changeStateToogle(true)}
                    >Вход</button>
                    <button
                        className={!authToggle ? 'active' : ''}
                        onClick={() => changeStateToogle(false)}
                    >Регистрация</button>
                </div>
                {authToggle ?
                    <AuthorizationForm hist={hist} />
                    :
                    <RegistrationForm />
                }
            </div>
        </section>
    )
}

export default Authorization;
