import React, { useState } from "react";
import { useForm } from "react-hook-form";

const AuthorizationForm = () => {
    const [statePassword, changeStatePassword] = useState(false);
    const {
        register,
        formState: { errors },
        handleSubmit
    } = useForm();

    const onSubmit = (data) => {
        alert(JSON.stringify(data));
    }

    return (
        <form className='authorization-form' onSubmit={handleSubmit(onSubmit)}>
            <label>
                Логин
                <input type='text' placeholder='Введите логин' required />
            </label>
            <label className='password'>
                Пароль
                <input type={statePassword ? 'text' : 'password'} className={statePassword ? 'active' : ''} placeholder='Введите пароль' required />
                <button className={statePassword ? 'active' : ''} onClick={() => changeStatePassword(!statePassword)}></button>
            </label>
            <div className='remember'>
                <label>
                    <input type='checkbox' />
                    Запомнить
                </label>
                <button>Забыли пароль?</button>
            </div>
            <input type='submit' />
        </form>
    )
}

export default AuthorizationForm;
