import React from "react";

class AuthorizationForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            statePassword: false
        }
    }

    showPassword = () => {
        this.setState({ statePassword: !this.state.statePassword });
    }

    render() {
        const { statePassword } = this.state;
        return (
            <form className='authorization-form'>
                <label>
                    Логин
                    <input type='text' placeholder='Введите логин' required />
                </label>
                <label className='password'>
                    Пароль
                    <input type={statePassword ? 'text' : 'password'} className={statePassword ? 'active' : ''} placeholder='Введите пароль' required />
                    <button className={statePassword ? 'active' : ''} onClick={this.showPassword}></button>
                </label>
                <div className='remember'>
                    <label>
                        <input type='checkbox' />
                        Запомнить
                    </label>
                    <button>Забыли пароль?</button>
                </div>
            </form>
        )
    }
}

export default AuthorizationForm;
