import { useLocation } from "react-router-dom";

const NewsPage = ({ news }) => {
    const location = useLocation();
    const currentLocation = location.pathname.substring(6);
    const addTitleToNews = news.map((article) => {
        return article.path === currentLocation &&
            { ...article.content[0], title: article.title, date: article.date }
    })

    console.log(addTitleToNews);

    return (
        <section className='news'>
            {addTitleToNews.map((article, i) => {
                return article &&
                    <div className='article' key={i}>
                        {
                            article.content.map((item, i) => (
                                <div className='article_block' key={i}>
                                    <img src={item.img} alt='news-pic' />
                                    <div>
                                        {
                                            item.texts.map((text, i) => (
                                                <p key={i}>{text}</p>
                                            ))
                                        }
                                    </div>
                                </div>
                            ))
                        }
                    </div>
            })}
        </section>
    )
}

export default NewsPage;
