import React from "react";
import { Link } from "react-router-dom";

import Header from "../header";
import Footer from "../footer";

class PrivacyInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            stateEditor: false
        }
    }

    handleOpenEditor = () => {
        this.setState({ stateEditor: true });
    }

    render() {
        const { stateEditor } = this.state;
        const { headerNav, footerNav, isLogged, authUser, handleLogOut } = this.props;

        return (
            <>
                <Header headerNav={headerNav} isLogged={isLogged} authUser={authUser} handleLogOut={handleLogOut} />
                <section className='personal_area'>
                    <nav>
                        <ul>
                            <Link to={`/personal-area/${authUser.login.toLowerCase()}/info`}><li>Личная информация</li></Link>
                            <Link to={`/personal-area/${authUser.login.toLowerCase()}/orders`}><li>Заказы</li></Link>
                            <Link to={`/personal-area/${authUser.login.toLowerCase()}/terms`}><li>Условия обслуживания</li></Link>
                        </ul>
                    </nav>
                    {
                        stateEditor ?
                            <div className='personal_info'>
                                <img src='/images/default-icon-product.svg' alt='user' />
                                <div className='about_person'>
                                    {authUser.firstName && authUser.lastName ? <p>{authUser.firstName} <span>{authUser.lastName}</span></p> : <p>Как Вас зовут?</p>}
                                    {authUser.address ? <p>{authUser.address}</p> : <p>Адрес: Неизвестно</p>}
                                    <div className='contacts'>
                                        {authUser.phone ? <p>{authUser.phone}</p> : <p>Телефон: <span>Неизвестно</span></p>}
                                        <p>
                                            Электронная почта:
                                            <span>{authUser.mail}</span>
                                        </p>
                                    </div>
                                    <button onClick={this.handleOpenEditor}></button>
                                </div>
                            </div>
                            :
                            <form>
                                <input type='file' />
                                <div className='edit__about_person'>
                                    <label>
                                        Имя:
                                        <input type='text' defaultValue={authUser.firstName ? authUser.firstName : ''} />
                                    </label>
                                    <label>
                                        Фамилия:
                                        <input type='text' defaultValue={authUser.lastName ? authUser.lastName : ''} />
                                    </label>
                                    <label>
                                        Адрес:
                                        <input type='text' defaultValue={authUser.address ? authUser.address : ''} />
                                    </label>
                                    <label>
                                        Телефон:
                                        <input type='text' defaultValue={authUser.phone ? authUser.phone : ''} />
                                    </label>
                                    <label>
                                        Электронная почта:
                                        <input type='text' defaultValue={authUser.mail} />
                                    </label>
                                </div>
                            </form>
                        }

                </section>
                <Footer footerNav={footerNav} />
            </>
        )
    }
}

export default PrivacyInfo;
