import React from "react";
import { Routes, Route } from "react-router-dom";

import Header from "./components/header";
import Main from "./components/main";
import Catalog from "./components/main/catalog";
import Footer from "./components/footer";

class App extends React.Component {
  constructor (props) {
    super(props);
    this.state = {
      chosenBrand: {}
    }
  }

  setChosenBrand = (product) => {
    this.setState({chosenBrand: product});
  }

  render() {

    const { chosenBrand } = this.state;

    return (
      <div className='wrapper'>
        <Header headerNav = {this.props.headerNav} />
        <Routes>
          <Route path='/' element={<Main {...this.props} />} />
          <Route path='/catalog' element={<Catalog {...this.props} setChosenBrand={this.setChosenBrand} />} />
          <Route path='/catalog/:brand' element={<BrandCatalog chosenBrand={chosenBrand} />} />
        </Routes>
        <Footer footerNav = {this.props.footerNav} />
      </div>
    )
  }
}

export default App;
