import React from "react";
import { Routes, Route, Navigate } from "react-router-dom";

import Wrapper from "./wrapper";
import AuthorizationContainer from "./components/authorization/authorization-container";
import RegistrationContainer from "./components/authorization/registration-container";
import PersonalArea from "./components/personal/area";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogged: false,
      authUser: null
    }
  }

  handleLogIn = (data) => {
    this.setState({ isLogged: true, authUser: data });
  }

  render() {
    const { isLogged, authUser } = this.state;

    return (
      <div className='wrapper'>
        <Routes>
          <Route path='*' element={<Wrapper {...this.props} isLogged={isLogged} authUser={authUser} />} />
          <Route path='/login/*' element={<AuthorizationContainer handleLogIn={this.handleLogIn} isLogged={isLogged} authUser={authUser} />} />
          <Route path='/registration/*' element={<RegistrationContainer handleLogIn={this.handleLogIn} />} />
          {isLogged ?
            <Route path='/personal-area/:user/*' element={<PrivacyInfo {...this.props} isLogged={isLogged} authUser={authUser} />} />
            :
            <Route path='/login/*' element={<AuthorizationContainer handleLogIn={this.handleLogIn} isLogged={isLogged} authUser={authUser} />} />
          }
        </Routes>
      </div>
    )
  }
}

export default App;
