import React from "react";
import { YMaps, Map, Clusterer, Placemark } from "react-yandex-maps";

class Contacts extends React.Component {
    render() {
        const mapSettings = {
            center: [47.629989, 43.146086],
            zoom: 7
        };

        const { stores } = this.props;

        const pointsVolgograd = stores.volgograd.map(store => {
            return store.coords;
        });

        const pointsAstrahan = stores.astrahan.map(store => {
            return store.coords;
        });

        const pointsVolojskiy = stores.volojskiy.map(store => {
            return store.coords;
        });

        const pointsRostov = stores.rostov.map(store => {
            return store.coords;
        });

        const allPoints = pointsVolgograd.concat(pointsAstrahan, pointsVolojskiy, pointsRostov);

        return (
            <section className='contacts'>

                <YMaps>
                    <Map defaultState={mapSettings} width='1710px' height='638px'>
                        <Clusterer
                            options={{
                                preset: 'islands#invertedVioletClusterIcons',
                                groupByCoordinates: false
                            }}
                        >
                            {allPoints.map((point, i) => (
                                <Placemark
                                    key={i}
                                    geometry={point}
                                    modules={[
                                        "geoObject.addon.balloon"
                                    ]}
                                    properties={{
                                        balloonContent: 'red'
                                    }}
                                    onClick={() => this.handlePoint(point)}
                                />
                            ))}
                        </Clusterer>
                    </Map>
                </YMaps>
            </section>
        )
    }
}

export default Contacts;
