import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

import SearchForm from '../main/search-form';

const url = 'http://avd-backend.veon-tech.ru';

const Catalog = () => {
    const [catalog, setCatalog] = useState([]);

    useEffect(() => {
        loadCarsBrandToCatalog();
    }, [])

    const loadCarsBrandToCatalog = async () => {
        const headers = {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
        };


        await fetch(`${url}/api/cars/brands`, {
            method: 'GET',
            headers
        })
            .then(response => response.json())
            .then(result => {
                let urlModels = new URL(url + '/api/cars/models');
                let catalogToState = [];

                result.brands.forEach(brand => {
                    fetch(urlModels + '?brand=' + brand, {
                        method: 'GET',
                        headers
                    })
                        .then(response => response.json())
                        .then(result => {
                            catalogToState.push({ logo: './images/brand-logo/geely-logo.svg', name: brand, models: result.models });
                            setCatalog([...catalog, ...catalogToState]);
                        })
                });
            })
    }

    console.log(catalog);

    return (
        <>
            <SearchForm catalog={catalog} />
            <div className='catalog'>
                {catalog.length > 0 && catalog.map((brand, i) => {
                    let brandName = brand.name.split(' ').join('-');
                    return <Link
                        to={`/catalog/${brandName.toLowerCase()}`}
                        className='catalog-product'
                        key={i}
                    >
                        <img src={brand.logo} alt='brand-logo' />
                        <h3>
                            {brand.name}
                        </h3>
                        <p>
                            {brand.models ?
                                brand.models.map((model, i) => (
                                    <span key={i}>{(i ? ', ' : '') + model}</span>
                                ))
                                :
                                ''
                            }
                        </p>
                    </Link>
                })}
            </div>
        </>
    )
}

export default Catalog;
