import React from "react";
import { Routes, Route, Navigate } from "react-router-dom";

import Wrapper from "./wrapper";
import AuthorizationContainer from "./components/authorization/authorization-container";
import RegistrationForm from "./components/authorization/registration-form";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogged: false,
      authUser: null
    }
  }

  handleLogIn = (data) => {
    this.setState({ isLogged: true, authUser: data });
  }

  render() {
    const { isLogged, authUser } = this.state;

    return (
      <div className='wrapper'>
        <Routes>
          <Route path='*' element={<Wrapper {...this.props} isLogged={isLogged} authUser={authUser} />} />
          <Route path='/login' element={<AuthorizationContainer handleLogIn={this.handleLogIn} />} />
          <Route path='/registration' element={<RegistrationContainer handleLogIn={this.handleLogIn} />} />
        </Routes>
      </div>
    )
  }
}

export default App;
