import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import ModelCatalog from "./model-catalog";

const ModelCatalogWrapper = ({ catalog, location }) => {
    const [currentCatalog, setCurrentCatalog] = useState([]);

    useEffect(() => {
        const currentLocation = location.pathname.split('/').pop().split('-').join(' ');

        const currentCatalog = catalog.length > 0 && catalog.map(item => {
            console.log(item);
            return item.models && { ...item, models: item.models.filter(model => model.toLowerCase() === currentLocation.toLowerCase()) }
        });
        const filterCatalog = currentCatalog.length > 0 && currentCatalog.filter(item => item && item.models.length > 0);

        console.log(currentCatalog);

        this.setState({ currentCatalog: filterCatalog[0] })
    }, [location.pathname])

    const setSparesCategoryClick = spares => {
        setCurrentCatalog(spares);
    }


    return (
        currentCatalog && <ModelCatalog location={location} currentCatalog={currentCatalog} setSparesCategoryClick={setSparesCategoryClick} />
    )
}

export default ModelCatalogWrapper;
