import React, { useState } from "react";
import { useForm } from "react-hook-form";

const AuthorizationForm = () => {
    const [statePassword, changeStatePassword] = useState(false);
    const {
        register,
        formState: { errors },
        handleSubmit,
        reset
    } = useForm({
        mode: 'onBlur'
    });

    const onSubmit = (data) => {
        alert(JSON.stringify(data));
        reset();
    }

    console.log(errors);

    return (
        <form className='authorization-form' onSubmit={handleSubmit(onSubmit)}>
            <label>
                Логин
                <input
                    type='text'
                    placeholder='Введите логин'
                    className={errors.login !== undefined ? 'error' : ''}
                    {...register('login', {
                        required: 'Это поле обязательно к заполнению.',

                    })}
                />
            </label>
            <div className='error-message'>
                {errors?.login && <p>{errors?.login?.message || 'Error!'}</p>}
            </div>
            <label className='password'>
                Пароль
                <input
                    type={statePassword ? 'text' : 'password'}
                    className={`${statePassword ? 'active' : ''} ${errors.password !== undefined ? 'error' : ''}`}
                    placeholder='Введите пароль'
                    {...register('password', {
                        required: 'Это поле обязательно к заполнению.',
                        minLength: {
                            value: 6,
                            message: 'Пароль должен содержать от 6 до 16 символов.'
                        },
                        maxLength: {
                            value: 16,
                            message: 'Пароль должен содержать от 6 до 16 символов.'
                        },
                        pattern: {
                            value: /[A-z]/,
                            message: 'Пароль должен содержать, как минимум одну Заглавную букву.'
                        }
                    })}
                />
                <p className={statePassword ? 'active' : ''} onClick={() => changeStatePassword(!statePassword)}></p>
            </label>
            <div className='error-message'>
                {errors?.password && <p>{errors?.password?.message || 'Error!'}</p>}
            </div>
            <div className='remember'>
                <label>
                    <input type='checkbox' />
                    Запомнить
                </label>
                <button>Забыли пароль?</button>
            </div>
            <input type='submit' value='Вход' />
        </form>
    )
}

export default AuthorizationForm;
