import React from "react";
import { YMaps, Map, Clusterer, Placemark } from "react-yandex-maps";

class Contacts extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            allStores: [],
        }
    }

    componentDidMount() {
        this.setState({ allStores: this.props.stores });
    }

    handleCityClick = e => {
        const { value } = e.target.dataset;
        const stores = this.state.allStores.map(store => store.value === value && store.selected !== store.selected )
        console.log(stores);

    }

    render() {
        const mapSettings = {
            center: [47.629989, 43.146086],
            zoom: 7
        };

        const { allStores } = this.state;

        return (
            <section className='contacts'>
                <div className='contacts-wrapper'>
                    {allStores.map((store, i) => (
                        <button onClick={this.handleCityClick} data-value={store.value} key={i}>Ð³. {store.city}</button>
                    ))}
                </div>
                <div className='map'>
                    <YMaps>
                        <Map state={mapSettings} width='100%' height='100%'>
                            <Clusterer
                                options={{
                                    preset: 'islands#invertedVioletClusterIcons',
                                    groupByCoordinates: false
                                }}
                            >
                                {/* {allStores.map((store, i) => (
                                    <Placemark
                                        key={i}
                                        geometry={store.coords}
                                        modules={[
                                            "geoObject.addon.balloon"
                                        ]}
                                        properties={{
                                            balloonContent: `
                                            <h2>${store.city}</h2>
                                            <h3>${store.title}</h3>
                                            <p>${store.phone}</p>
                                        `
                                        }}
                                    />
                                ))} */}
                            </Clusterer>
                        </Map>
                    </YMaps>
                </div>
            </section>
        )
    }
}

export default Contacts;
