import React from "react";
import SearchForm from "../main/search-form";
import AccordionAnalogue from "./accordion-analogue";
import AccordionAsideCatalog from "./accordion-aside";

class ModelCatalog extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            chosenProductCategory: null,
            allProducts: []
        }
    }

    // componentDidMount() {
    //     const { currentCatalog } = this.props;
    //     const { allProducts } = this.state;

    //     currentCatalog.models[0].spares.map(part => (
    //         part.categories && part.categories.map(category => (
    //             category.products.map(product => (
    //                 product.parts && product.parts.map(part => (
    //                     allProducts.push(part)
    //                 ))
    //             ))
    //         ))
    //     ));

    //     this.setState({ allProducts: allProducts });
    // }

    handleSparesCategoryClick = e => {
        const { value } = e.target.dataset;
        const { currentCatalog } = this.props;
        const chosenCategorySpares = {
            ...currentCatalog, models: [
                {
                    model: currentCatalog.models[0].model, spares: currentCatalog.models[0].spares.map(item => (
                        item.value === value ? { ...item, selected: !item.selected } : { ...item }
                    ))
                }
            ]
        }

        this.props.setSparesCategoryClick(chosenCategorySpares);
    }

    setChosenProductCategories = product => {
        this.setState({ chosenProductCategory: product });
    }

    handleProductClick = e => {
        const { value } = e.target.dataset;
        const { chosenProductCategory } = this.state;
        const { allProducts } = this.state;

        const openProductAnalogue = chosenProductCategory && {
            ...chosenProductCategory, parts: chosenProductCategory.parts.map(part => (
                part.article === value ? { ...part, selected: !part.selected } : { ...part, selected: false }
            ))
        }

        const openAllProductsAnalogue = allProducts.map(product => (
            product.article === value ? { ...product, selected: !product.selected } : { ...product, selected: false }
        ))

        this.setState({ chosenProductCategory: openProductAnalogue, allProducts: openAllProductsAnalogue });
    }

    render() {
        const { chosenProductCategory, allProducts } = this.state;
        const { currentCatalog, location, setProductForCart } = this.props;

        console.log(currentCatalog);

        return (
            <main>
                <SearchForm {...this.props} />
                {allProducts.length > 0 &&
                    <div className='model-catalog'>
                        <h1>{currentCatalog.name + ', ' + currentCatalog.models}</h1>
                        <div className='model-catalog__wrapper'>
                            <aside>
                                <ul>
                                    {currentCatalog.models[0].spares && currentCatalog.models[0].spares.map((item, i) => (
                                        <li className={`${item.selected ? 'open' : ''}`} key={i}>
                                            {item.name}
                                            {item.selected &&
                                                <AccordionAsideCatalog spares={item} setChosenProductCategories={this.setChosenProductCategories} />
                                            }
                                            <button
                                                className={`model-catalog_button ${item.selected ? 'active' : ''}`}
                                                data-value={item.value}
                                                onClick={this.handleSparesCategoryClick}
                                            >
                                                <span
                                                    data-value={item.value}
                                                    onClick={this.handleSparesCategoryClick}>
                                                </span>
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            </aside>
                            <div>
                                {chosenProductCategory ?
                                    chosenProductCategory.parts.map((part, i) => (
                                        <div className='product-group__wrapper' key={i}>
                                            <div className='product-group'>
                                                <div>
                                                    <h2>{part.title}</h2>
                                                    <p>Артикул: {part.article}</p>
                                                    <p>Производитель: {part.manufacturer}</p>
                                                </div>
                                                {part.selected ?
                                                    <button className={part.selected ? 'active' : ''} data-value={part.article} onClick={this.handleProductClick}>Скрыть все товары</button>
                                                    :
                                                    <button className={part.selected ? 'active' : ''} data-value={part.article} onClick={this.handleProductClick}>Показать все товары</button>
                                                }
                                            </div>
                                            <div className={`analogue-products ${part.selected ? 'open' : 'hidden'}`}>
                                                <AccordionAnalogue setProductForCart={setProductForCart} selected={part.selected} analogue={part.analogue} />
                                            </div>
                                        </div>
                                    ))
                                    :
                                    allProducts.map((product, i) => (
                                        <div className='product-group__wrapper' key={i}>
                                            <div className='product-group'>
                                                <div>
                                                    <h2>{product.title}</h2>
                                                    <p>Артикул: {product.article}</p>
                                                    <p>Производитель: {product.manufacturer}</p>
                                                </div>
                                                {product.selected ?
                                                    <button className={product.selected ? 'active' : ''} data-value={product.article} onClick={this.handleProductClick}>Скрыть все товары</button>
                                                    :
                                                    <button className={product.selected ? 'active' : ''} data-value={product.article} onClick={this.handleProductClick}>Показать все товары</button>
                                                }
                                            </div>
                                            <div className={`analogue-products ${product.selected ? 'open' : 'hidden'}`}>
                                                <AccordionAnalogue setProductForCart={setProductForCart} location={location.pathname} selected={product.selected} analogue={product.analogue} />
                                            </div>
                                        </div>
                                    ))
                                }
                            </div>
                        </div>
                    </div>}
            </main>
        )
    }
}

export default ModelCatalog;
