import React from "react";
import { Link } from 'react-router-dom';

class Header extends React.Component {
    render() {
        const { headerNav, isLogged, authUser } = this.props;
        return (
            <header>
                <div className='header__wrapper'>
                    <nav>
                        <Link to='/'><img src='/images/logo.svg' alt='logo' /></Link>
                        <ul>
                            {headerNav.map((item, i) => (
                                <Link to={item.path} key={i}><li>{item.name}</li></Link>
                            ))}
                        </ul>
                    </nav>
                    <div className='header_buttons'>
                        {
                            isLogged ?
                                <div>
                                    <button>{authUser.login}</button>
                                    <Link to={`/personal-area/${authUser.login.toLowerCase()}/info`} className='authorization_button'>{authUser.login}</Link>
                                </div>
                                :
                                <Link to='/login' className='authorization_button'>Вход</Link>
                        }
                        <button className='cart_button'>120,00 руб</button>
                    </div>
                </div>
            </header>
        )
    }
}

export default Header;
