import React from "react";
import { YMaps, Map, Clusterer, Placemark } from "react-yandex-maps";

class Contacts extends React.Component {
    constructor(props) {
        super(props);
        this.state ={
            allStores: [],
        }
    }

    componentDidMount() {
        this.setState({allStores: this.props.stores});
    }

    render() {
        const mapSettings = {
            center: [47.629989, 43.146086],
            zoom: 7
        };

        const { allStores } = this.state;

        return (
            <section className='contacts'>
                <div>
                    <h2>г. Волгоград</h2>
                    <div className='contacts-wrapper'>
                        {storesVolgograd.map((store, i) => (
                            <div key={i}>
                                <p>{store.title}</p>
                                <p>{store.phone}</p>
                            </div>
                        ))}
                    </div>
                </div>
                <div className='contacts-footer'>
                    <div>
                        <h2>г. Воложский</h2>
                        <div className='contacts-wrapper'>
                            {storesVolojskiy.map((store, i) => (
                                <div key={i}>
                                    <p>{store.title}</p>
                                    <p>Телефон: {store.phone}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                    <div>
                        <h2>г. Астрахань</h2>
                        <div className='contacts-wrapper'>
                            {storesAstrahan.map((store, i) => (
                                <div key={i}>
                                    <p>{store.title}</p>
                                    <p>{store.phone}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                    <div>
                        <h2>г. Ростов - на - Дону</h2>
                        <div className='contacts-wrapper'>
                            {storesRostov.map((store, i) => (
                                <div key={i}>
                                    <p>{store.title}</p>
                                    <p>{store.phone}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
                <div className='map'>
                    <YMaps>
                        <Map state={mapSettings} width='100%' height='100%'>
                            <Clusterer
                                options={{
                                    preset: 'islands#invertedVioletClusterIcons',
                                    groupByCoordinates: false
                                }}
                            >
                                {allStores.map((store, i) => (
                                    <Placemark
                                        key={i}
                                        geometry={store.coords}
                                        modules={[
                                            "geoObject.addon.balloon"
                                        ]}
                                        properties={{
                                            balloonContent: `
                                            <h2>${store.city}</h2>
                                            <h3>${store.title}</h3>
                                            <p>${store.phone}</p>
                                        `
                                        }}
                                    />
                                ))}
                            </Clusterer>
                        </Map>
                    </YMaps>
                </div>
            </section>
        )
    }
}

export default Contacts;
