import React from "react";
import { Link } from 'react-router-dom';

import SearchForm from "../main/search-form";

class Catalog extends React.Component {

    handleClickBrand = (product) => {
        this.props.setChosenBrand(product);
    }

    render() {
        const { catalog } = this.props;
        return (
            <>
                <SearchForm {...this.props} />
                <div className='catalog'>
                    {catalog.map((brand, i) => (
                        <Link
                            to={`/catalog/${brand.name.toLowerCase()}`}
                            className='catalog-product'
                            key={i}
                        >
                            <div onClick={() => this.handleClickBrand(brand)}>
                                <img src={brand.logo} alt='brand-logo' />
                                <h3>
                                    {brand.name}
                                </h3>
                                <p>
                                    {brand.models.map((model, i) => (
                                        <span key={i}>{(i ? ', ' : '') + model}</span>
                                    ))}
                                </p>
                            </div>
                        </Link>
                    ))}
                </div>
            </>
        )
    }
}

export default Catalog;
