import React from "react";
import ModelCatalog from "./model-catalog";

class ModelCatalogWrapper extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentCatalog: null
        }
    }

    componentDidMount() {
        const { location, catalog } = this.props;
        const currentLocation = location.pathname.split('/').pop().split('-').join(' ');

        const currentCatalog = catalog.map(item => {
            return item.models && { ...item, models: item.models.filter(model => model.model.toUpperCase() === currentLocation.toUpperCase()) }
        });
        const filterCatalog = currentCatalog.filter(item => item && item.models.length > 0);

        this.setState({ currentCatalog: filterCatalog[0] })
    }

    setSparesCategoryClick = spares => {
        this.setState({ currentCatalog: spares });
    }

    render() {
        const { currentCatalog } = this.state;

        return (
            currentCatalog && <ModelCatalog {...this.props} currentCatalog={currentCatalog} setSparesCategoryClick={this.setSparesCategoryClick} />
        )
    }
}

export default ModelCatalogWrapper;
