import React from "react";
import { Link, Route, Routes, useLocation } from "react-router-dom";

import AuthorizationForm from "./authorization-form";
import AuthorizationToggle from "./authorization-toggle";
import RegistrationForm from "./registration-form";

const Authorization = () => {

    const location = useLocation();

    let currentLocation = location.pathname.split('/').pop();

    return (
        <section className='authorization__wrapper'>
            <div className='authorization__img'>
                <Link to='/'><img src='../images/logo.svg' alt='logo' /></Link>
            </div>
            <div className='authorization__form'>
                <AuthorizationToggle currentLocation={currentLocation} />
                <Routes>
                    <Route path='/login/authorization' element={<AuthorizationForm />} />
                    <Route path='/login/registration' element={<RegistrationForm />} />
                </Routes>
            </div>
        </section>
    )
}

export default Authorization;
