import React from "react";
import { Slider } from "react-flex-slick";

import SearchForm from "./main/search-form";

class Main extends React.Component {
    render() {
        const settings = {
            dots: false,
            infinite: true,
            transitionSpeed: 500,
            slidesToShow: 1,
            slidesToScroll: 1
        };

        return (
            <main>
                <SearchForm catalog={this.props.catalog} />
                <section className='main__service-wrapper'>
                    <div className='main__service'>
                        <div className='service__description'>
                            <h1>Запчасти по доступной цене</h1>
                            <ul>
                                <li>Доставка в любое место</li>
                                <li>Консультация специалиста</li>
                            </ul>
                        </div>
                        <div className='order-product'>
                            <Slider {...settings}>
                                {this.props.products.map((product, i) => (
                                    <div className='product-cart' key={i}>
                                        <div className='product-cart__img-wrapper'>
                                            <img src={product.image} alt='icon-product' />
                                        </div>
                                        <div>
                                            <h3>{product.name}</h3>
                                            <p>{product.brand}</p>
                                            <button>Заказать</button>
                                        </div>
                                    </div>
                                ))}
                            </Slider>
                        </div>
                        <div className='main-bg'></div>
                    </div>
                </section>
            </main>
        )
    }
}

export default Main;
