import React from "react";
import { Link } from "react-router-dom";

import AuthorizationForm from "./authorization-form";
import RegistrationForm from "./registration-form";

class Authorization extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            authToggle: true,
            newAuthorization: {} 
        }
    }

    openAuthorizationForm = () => {
        this.setState({ authToggle: true });
    }

    openRegistrationForm = () => {
        this.setState({ authToggle: false });
    }

    handleNewAuthorization = (data) => {
        this.setState({newAuthorization: data});
    }

    render() {
        const { authToggle } = this.state;
        console.log(this.state.newAuthorization);

        return (
            <section className='authorization__wrapper'>
                <div className='authorization__img'>
                    <Link to='/'><img src='../images/logo.svg' alt='logo' /></Link>
                </div>
                <div className='authorization-form__wrapper'>
                    <div className='authorization__toggle'>
                        <button
                            className={authToggle ? 'active' : ''}
                            onClick={this.openAuthorizationForm}
                        >Вход</button>
                        <button
                            className={!authToggle ? 'active' : ''}
                            onClick={this.openRegistrationForm}
                        >Регистрация</button>
                    </div>
                    {authToggle ?
                        <AuthorizationForm handleNewAuthorization={this.handleNewAuthorization} />
                        :
                        <RegistrationForm />
                    }
                </div>
            </section>
        )

    }
}

export default Authorization;
